    public static ReductionPotentialSearcher getInstance() {
        if(instance == null) {
            instance = new ReductionPotentialSearcher();
        }
        return instance;
    }

    public ReductionPotential search(String query) {
        for(ReductionPotential p : reductionPotentials) {
            if(p.getReaction().contains(query)) return p;
        }
        return null;
    }

    public MessageEmbed fancySearch(String query) {
        EmbedBuilder builder = new BotEmbedBuilder();
        EquationFormatter formatter = new EquationFormatter();
        boolean found = false;
        for(ReductionPotential rp : reductionPotentials) {
            if(rp.getReaction().contains(query)) {
                found = true;
                builder.addField(formatter.formatEquation(rp.getReaction()),
                        Float.toString(rp.getPotential()) + " V", false);
            }
        }
        if(found) {
            builder.setTitle(String.format(":battery: Reduction Potential Search Results for \"%s\"", query), null);
            builder.setDescription("Reduction Potential - **E₀**");
        } else {
            builder.setTitle(String.format("No Results Found for \"%s\"", query), null);
        }
        return builder.build();
    }
